package a_if_else;

public class B_IfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int numero = 3;

		if (numero > 5) {
		    System.out.println("O número é maior que 5.");
		} else {
		    System.out.println("O número não é maior que 5.");
		}
	}

}
