package b_ternarioSwitch;

public class C_SwitchString {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String cor = "vermelho";

		switch (cor) {
		    case "vermelho":
		        System.out.println("A cor é vermelha.");
		        break;
		    case "azul":
		        System.out.println("A cor é azul.");
		        break;
		    case "verde":
		        System.out.println("A cor é verde.");
		        break;
		    default:
		        System.out.println("Cor desconhecida.");
		}

	}

}
